import * as R from 'ramda'

import {
ADD_PHONE_TO_BASKET,
DEL_PHONE_TO_BASKET,
REMOVE_FROM_PHONE_BASKET,
CLEAN_BASKET
} 
from 'actionTypes'

const initialState = []

export default (state = initialState,  {type,payload,id}) => {
    switch (type) {
        case ADD_PHONE_TO_BASKET:
            return R.append(payload,state)
           
        case REMOVE_FROM_PHONE_BASKET:
            return R.without(R.of(payload),state)
         case DEL_PHONE_TO_BASKET:
            const index = R.indexOf((id),payload)
            if (index > -1) {
                return R.remove(index,1, payload);
              
            }
            return 
            case CLEAN_BASKET:
            return initialState
        default:
            return state
    }
}