import * as R from 'ramda'

import {
  FETCH_PHONES_SUCCESS,
  LOAD_MORE_PHONES_SUCCESS,
  FETCH_PHONES_BY_ID_SUCCESS,
  LOCAL_BASKET_SUCCESS

} from 'actionTypes'

const initialState = {}

export default (state = initialState, { type, payload, offset }) => {


  console.log('offset', offset)

  switch (type) {
    case FETCH_PHONES_SUCCESS:
      const newValues = R.indexBy(R.prop('id'), payload)
      return R.merge(state, newValues)
    case LOAD_MORE_PHONES_SUCCESS:

      const moreValues = R.indexBy(R.prop('id'), payload)

      console.log('payload', payload)
      console.log('moreValues', moreValues)

      return R.merge(state, moreValues)
    case LOCAL_BASKET_SUCCESS:
      const reValues = R.indexBy(R.prop('id'), payload)
      return R.merge(state, reValues)

    // return R.merge(state, R.concat(state, reValues))


    case FETCH_PHONES_BY_ID_SUCCESS:
      console.log(payload)
      return R.assoc(payload.id, payload, state)
    default:
      return state

  }

}